-- ----------------------------
-- Table structure for message
-- ----------------------------
DROP TABLE IF EXISTS `message`;
CREATE TABLE `message`  (
  `mgeId` bigint(20) NOT NULL AUTO_INCREMENT,
  `userId` bigint(20) NULL DEFAULT NULL,
  `senderId` bigint(20) NULL DEFAULT NULL,
  `mainTitle` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL,
  `createTime` timestamp NULL DEFAULT NULL,
  `status` int(11) NULL DEFAULT NULL,
  `mgeUrl` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT '' COMMENT '跳转到回复消息的页面',
  `repId` bigint(20) NULL DEFAULT NULL COMMENT '被回复消息的Id',
  `comId` bigint(20) NULL DEFAULT NULL COMMENT '回复消息的评论id',
  PRIMARY KEY (`mgeId`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 36 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = 'userId 是消息接受者id\r\nsenderId 是消息发送者id\r\n0 为未读 1为已读' ROW_FORMAT = Compact;

