  `areaCode` varchar(16) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '地区',
  `status` int(11) NULL DEFAULT NULL COMMENT '删除状态(0删除 1存在)',
